plugin.phpcode:
if( $vbulletin->options['antishoutadvanced']
    && $vbulletin->GPC['title'] )
{
  $ash_text =& $edit['title'];

  // case pattern
  $ash_case_pattern =
    "["
    . mb_ereg_replace(
        "[\n\t\r\s]",
        "",
        $vbulletin->options['antishoutadvanced_letters'],
        'm'
      )
    . "]{"
    . $vbulletin->options['antishoutadvanced_num']
    . ",}";

  if( mb_ereg( $ash_case_pattern, $ash_text ) )
  {
    $ash_text = mb_strtolower( $ash_text );
  }

  // other patterns
  $ash_patterns = array(
      "/\?+/",
      "/\!+/",
      "/\.\.\.+/",
      "/^\!+/",
      "/^ +/",
      "/ +$/",
    );

  $ash_replaces = array(
      "?",
      "!",
      "...",
      "",
      "",
      "",
    );

  $ash_text = preg_replace(
      $ash_patterns,
      $ash_replaces,
      $ash_text
    );

  unset( $ash_text );
}

